INSERT INTO `comments_metal_resource_table` VALUES ('0163de1e-f5fd-11e3-9e90-782bcbe689ee', 'http://unews.fx678.com/union/ifx/news.asp?newsid=201406171611371226&column=hjsc', '\r美联储决议会议开启，黄金进一步脱离三周高位', '2014-06-17 00:00:00', '黄金自昨日所创触及的三周高位进一步下跌，因美联储今日开始为期两天的货币政策会议，在决议宣布前市场谨慎，加上投资者重新评估伊拉克及乌俄局势。', 'METAL', 'SYNET', null);
