INSERT INTO `comments_news_resource_table` VALUES ('c97f6c61-fd02-11e3-a84d-782bcbe689ee', '', '阿根廷工业生产预测 -4.5% 相对 -4.9%', '2014-06-26 15:23:36', '星期三官方数据显示,上月阿根廷工业生产超预期下跌.国家统计和人口普查研究所在报告中称,阿根廷工业生产下跌到-4.9%,上月到-4.0%分析家之前预计,上月阿根廷工业生产将下跌到-4.5%.									', 'FOREX', 'ADSNET');
