INSERT INTO `comments_stock_resource_table` VALUES ('ce350530-fd02-11e3-b71a-782bcbe689ee', 'http://research.jfinfo.com/news/20140626/00388670.shtml', '巨丰视角：抢钱行情延续', '2014-06-26 11:04:00', '\r				随着新股的陆续上市，后市炒作热潮或将退却，要谨防次新股的冲高回落。而对于新股，上市前几个交易日，风险较小，仍可重点关注……\r				', 'STOCK', 'JFNET');
