INSERT INTO comments_metal_resource_table VALUES ('4e529450-0125-11e4-9816-d8d385043fbf', 'http://www.91jin.com/p-1404200277298.html', '铜评：铜价破位上行 铜牛身影重现', '2014-07-01 00:00:00', '隔夜公布的美国房地产数据表明美国楼市呈现稳中向好态势，今日上午公布的中国制造业指数表明国内经济企稳迹象明显，中国政府再次释放宽松政策加码信号，市场情绪偏乐观，铜价继续演绎...', 'METAL', 'GXNET', null);
