INSERT INTO comments_metal_resource_table VALUES ('5605b5ae-0125-11e4-89f6-d8d385043fbf', 'http://unews.fx678.com/union/ifx/news.asp?newsid=201407011906001783&column=hjsc', '\r黄金站稳1321，非农前逢低做多为宜', '2014-07-01 00:00:00', '周二(7月1日)欧市盘中，现货黄金交投于1325美元/盎司附近，隔夜先抑后扬上破1321美元/盎司短期关键阻力预示着多头重新回归并且控制市场主动性。新一轮上行趋势或就此展开，非农前回调做多为宜。', 'METAL', 'SYNET', null);
