INSERT INTO `comments_metal_resource_table` VALUES ('0a691dc0-ef75-11e3-9509-782bcbe689ee', 'http://unews.fx678.com/union/ifx/news.asp?newsid=201406090747141838&column=hjsc', '\r业内人士指出：在监管加强背景下，铜等矿石融资将越来越难做', '2014-06-09 00:00:00', '关于中国铜融资青岛港口调查事件，业内人士指出在监管加强及信用证贸易融资的利润空间逐渐缩小的背景下，虚假贸易融资越来越难做。\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n    <script type=\"text/javascript\" src=\"http://js.tongji.linezing.com/881408/tongji.js\"></script><noscript><a href=\"http://www.linezing.com\"><img src=\"http://img.tongji.linezing.com/881408/tongji.gif\"/></a></noscript>\r\n\r\n</body>\r\n</html>\r', 'METAL', 'SYNET', null);
