INSERT INTO morning_financenews_resource_table VALUES ('3d4576f4-ff86-11e3-bf07-bda7f6113c3a', 'http://www.nbd.com.cn/articles/2014-06-27/844805.html', '去年全国社保基金投资收益685.87亿收益率6.2%', '2014-06-27 20:18:00', '记者27日从全国社会保障基金理事会获悉，经审计，去年度全国社保基金投资收益685.87亿元，投资收益率6.2%，超过同期2.6%的通货膨胀率。去年末，基金会管理的基金资产总额12415.64亿元。(记者何雨欣、侯雪静)', 'CHINA', 'NBDNET', 'http://image1.nbd.com.cn/uploads/articles/thumbnails/96736/30.thumb_hs.jpg');
